const fs=require("fs"),path=require("path"),g=document.getElementById.bind(document),creds=JSON.parse(fs.readFileSync(path.join(__dirname,"./credentials.json")));function run(){const username=g("username"),password=g("password");if(creds.username&&creds.password&&username&&(username.value=creds.username,password.value=creds.password,document.getElementsByClassName("btn")[0].click()),username){function saveAutoLogin(){creds.username=username.value,creds.password=password.value,fs.writeFile(path.join(__dirname,"./credentials.json"),JSON.stringify(creds),()=>{})}document.getElementsByClassName("btn")[0].onclick=saveAutoLogin}}window.addEventListener("DOMContentLoaded",run);